import React, { useState } from 'react';

const JobSearch = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [location, setLocation] = useState('');
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchTerm.trim()) {
      setError('Please enter a job title or keyword');
      return;
    }

    try {
      setLoading(true);
      setError('');
      // TODO: Implement job search logic here
      // const response = await searchJobs(searchTerm, location);
      // setJobs(response.data);

      // Mock data for now
      setJobs([
        {
          id: 1,
          title: 'Software Engineer',
          company: 'Tech Corp',
          location: 'New York, NY',
          description: 'Looking for a skilled software engineer...',
        },
        {
          id: 2,
          title: 'Frontend Developer',
          company: 'Web Solutions',
          location: 'San Francisco, CA',
          description: 'Join our team of frontend developers...',
        },
      ]);
    } catch (err) {
      setError('Failed to search for jobs. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Job Search</h1>

        <div className="bg-white p-6 rounded-lg shadow-md mb-8">
          <form onSubmit={handleSearch}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
              <div>
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="search">
                  Job Title or Keywords
                </label>
                <input
                  type="text"
                  id="search"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  placeholder="e.g. Software Engineer"
                />
              </div>
              <div>
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="location">
                  Location
                </label>
                <input
                  type="text"
                  id="location"
                  value={location}
                  onChange={(e) => setLocation(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  placeholder="e.g. New York, NY"
                />
              </div>
            </div>

            {error && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                {error}
              </div>
            )}

            <button
              type="submit"
              disabled={loading}
              className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              {loading ? 'Searching...' : 'Search Jobs'}
            </button>
          </form>
        </div>

        <div className="space-y-4">
          {jobs.map((job) => (
            <div key={job.id} className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-xl font-semibold mb-2">{job.title}</h2>
              <p className="text-gray-600 mb-2">{job.company} - {job.location}</p>
              <p className="text-gray-700">{job.description}</p>
            </div>
          ))}

          {jobs.length === 0 && !loading && (
            <p className="text-center text-gray-600">No jobs found. Try a different search.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default JobSearch;
