import axios from 'axios';

const API_URL = 'http://localhost:8000/api';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add a request interceptor to add the auth token to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

export const auth = {
  register: async (username, email, password, role, firstName = '', lastName = '') => {
    try {
      const response = await api.post('/auth/register/', {
        username,
        email,
        password,
        role,
        first_name: firstName,
        last_name: lastName,
      });
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  login: async (username, password) => {
    try {
      const response = await api.post('/auth/login/', {
        username,
        password,
      });
      if (response.data.access) {
        localStorage.setItem('token', response.data.access);
      }
      return response.data;
    } catch (error) {
      if (error.response?.status === 401) {
        throw { detail: 'Invalid username or password' };
      }
      throw error.response?.data || error.message;
    }
  },

  logout: () => {
    localStorage.removeItem('token');
  },

  getCurrentUser: async () => {
    try {
      const response = await api.get('/auth/profile/');
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },
};

export const resume = {
  upload: async (file) => {
    try {
      const formData = new FormData();
      formData.append('resume', file);
      const response = await api.post('/resumes/upload/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  getAnalysis: async (resumeId) => {
    try {
      const response = await api.get(`/resumes/${resumeId}/analysis/`);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },
};

export const jobs = {
  search: async (searchTerm, location) => {
    try {
      const response = await api.get('/jobs/search/', {
        params: {
          search: searchTerm,
          location,
        },
      });
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  getJobDetails: async (jobId) => {
    try {
      const response = await api.get(`/jobs/${jobId}/`);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },
};

export const profile = {
  update: async (userData) => {
    try {
      const response = await api.put('/auth/profile/', userData);
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },

  changePassword: async (currentPassword, newPassword) => {
    try {
      const response = await api.post('/auth/password-reset/confirm/', {
        current_password: currentPassword,
        new_password: newPassword,
      });
      return response.data;
    } catch (error) {
      throw error.response?.data || error.message;
    }
  },
};

export default api;
